import React, { Component } from "react";

class Product extends Component {
  state = {
    name: this.props.product.name,
    count: this.props.product.count,
  };

  getClasses() {
    return this.state.count === 0
      ? "badge text-bg-warning m-2"
      : "badge text-bg-primary m-2";
  }

  incrementHandler = (num) => {
    this.setState({ count: this.state.count + num });
  };

  render() {
    return (
      <div>
        <div className="row"><div className="col-2">
          <span>{this.state.name}</span>
        </div>
        <div className="col">
          <span className={this.getClasses()}>{this.state.count}</span>
          <button
            onClick={(_) => this.incrementHandler(2)}
            className="btn btn-primary btn-sm"
          >
            +
          </button>
          <span>
            <i class="fa-solid fa-trash m-2"></i>
          </span>
        </div></div>
      </div>
    );
  }
}

export default Product;
